import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Product } from '../types';
import { productAPI, cartAPI } from '../api';
import ChristmasCarousel from './ChristmasCarousel';

interface ProductGridProps {
  onCartUpdate: () => void;
}

const ProductGrid: React.FC<ProductGridProps> = ({ onCartUpdate }) => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('Loading products...');
      const productsData = await productAPI.getProducts();
      console.log('Products loaded:', productsData);
      setProducts(productsData);
    } catch (error) {
      console.error('Error loading products:', error);
      setError('Failed to load products. Please check if the backend server is running.');
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = async (productId: string, event: React.MouseEvent) => {
    event.stopPropagation();
    try {
      await cartAPI.addToCart(productId);
      onCartUpdate();
      alert('Product added to cart!');
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Error adding product to cart');
    }
  };

  const handleProductClick = (productId: string) => {
    navigate(`/product/${productId}`);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading Christmas deals...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-content">
          <h2>‚ùå Unable to Load Products</h2>
          <p>{error}</p>
          <p>Make sure the backend server is running on http://localhost:5000</p>
          <button 
            className="back-home-btn"
            onClick={loadProducts}
          >
            üîÑ Try Again
          </button>
        </div>
      </div>
    );
  }

  if (products.length === 0) {
    return (
      <div className="error-container">
        <div className="error-content">
          <h2>üéÑ No Products Found</h2>
          <p>No products are available at the moment.</p>
          <button 
            className="back-home-btn"
            onClick={loadProducts}
          >
            üîÑ Refresh
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="home-page">
      {/* Christmas Carousel */}
      <ChristmasCarousel />
      
      {/* Christmas Theme Header */}
      <div className="christmas-header">
        <h2 className="festive-title">üéÖ Christmas Special Offers üéÑ</h2>
        <p className="festive-subtitle">Discover amazing deals for the holiday season!</p>
      </div>

      {/* Products Grid */}
      <div className="product-grid">
        {products.map((product: Product) => (
          <div 
            key={product.id} 
            className="product-card festive-card"
            onClick={() => handleProductClick(product.id)}
          >
            {/* Christmas Badge */}
            <div className="christmas-badge">üéÑ XMAS SALE</div>
            
            <div className="product-image">
              <img 
                src={product.image} 
                alt={product.name}
                onError={(e) => {
                  // Fallback if image fails to load
                  (e.target as HTMLImageElement).src = 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=500&h=500&fit=crop';
                }}
              />
            </div>
            <h3 className="product-name">{product.name}</h3>
            <p className="product-description">{product.description}</p>
            <div className="product-price festive-price">${product.price.toFixed(2)}</div>
            <button 
              className="add-to-cart-btn festive-cart-btn"
              onClick={(e) => handleAddToCart(product.id, e)}
            >
              üéÅ Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
