// src/components/Cart.tsx
import React, { useState, useCallback } from 'react';
import { Cart as CartType } from '../types';
import { cartAPI } from '../api';

/* -------------------------------------------------------------------------- */
/* Inline SVG Icons (same size / stroke as lucide-react)                     */
/* -------------------------------------------------------------------------- */
const IconShoppingCart = ({ size = 24 }: { size?: number }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth={1.5}>
    <path d="M9 22a1 1 0 1 0 0-2 1 1 0 0 0 0 2zM20 22a1 1 0 1 0 0-2 1 1 0 0 0 0 2z" />
    <path d="M1 1h4l2.22 13.29a2 2 0 0 0 2 1.71h9.56a2 2 0 0 0 2-1.71L23 6H6" />
  </svg>
);

const IconTrash = ({ size = 16 }: { size?: number }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth={2}>
    <path d="M3 6h18M8 6V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2m-8 0h10M10 11v6M14 11v6M5 6l1.5 12a2 2 0 0 0 2 1.88h7a2 2 0 0 0 2-1.88L19 6" />
  </svg>
);

const IconPlus = ({ size = 16 }: { size?: number }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth={2.5}>
    <path d="M12 5v14M5 12h14" />
  </svg>
);

const IconMinus = ({ size = 16 }: { size?: number }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth={2.5}>
    <path d="M5 12h14" />
  </svg>
);

/* -------------------------------------------------------------------------- */
/* Props                                                                      */
/* -------------------------------------------------------------------------- */
interface CartProps {
  cart: CartType;
  onCartUpdate: () => void;
  onProceedToCheckout?: () => void;
}

/* -------------------------------------------------------------------------- */
/* Cart Component                                                             */
/* -------------------------------------------------------------------------- */
const Cart: React.FC<CartProps> = ({ cart, onCartUpdate, onProceedToCheckout }) => {
  const [updating, setUpdating] = useState<Set<string>>(new Set());

  /* ---------------------------------------------------------------------- */
  /* Handlers                                                               */
  /* ---------------------------------------------------------------------- */
  const startUpdate = (id: string) => setUpdating(prev => new Set(prev).add(id));
  const endUpdate = (id: string) =>
    setUpdating(prev => {
      const next = new Set(prev);
      next.delete(id);
      return next;
    });

  const handleRemoveItem = useCallback(
    async (cartItemId: string) => {
      startUpdate(cartItemId);
      try {
        await cartAPI.removeFromCart(cartItemId);
        onCartUpdate();
      } catch (err) {
        console.error(err);
        alert('Failed to remove item. Please try again.');
      } finally {
        endUpdate(cartItemId);
      }
    },
    [onCartUpdate]
  );

  const handleUpdateQuantity = useCallback(
    async (cartItemId: string, newQty: number) => {
      if (newQty < 1) {
        handleRemoveItem(cartItemId);
        return;
      }

      startUpdate(cartItemId);
      try {
        const item = cart.items.find(i => i.id === cartItemId);
        if (!item) return;

        // Optimistic UI
        const originalQty = item.quantity;
        item.quantity = newQty;
        item.itemTotal = item.price * newQty;
        cart.total = cart.items.reduce((s, i) => s + i.itemTotal, 0);

        await cartAPI.removeFromCart(cartItemId);
        await cartAPI.addToCart(item.productId, newQty);
        onCartUpdate();
      } catch (err) {
        console.error(err);
        alert('Failed to update quantity. Please try again.');
        onCartUpdate(); // refetch to restore correct state
      } finally {
        endUpdate(cartItemId);
      }
    },
    [cart, onCartUpdate, handleRemoveItem]
  );

  /* ---------------------------------------------------------------------- */
  /* Empty State                                                            */
  /* ---------------------------------------------------------------------- */
  if (cart.items.length === 0) {
    return (
      <div
        style={{
          textAlign: 'center',
          padding: '4rem 2rem',
          color: '#6b7280',
        }}
      >
        <div style={{ marginBottom: '1.5rem', opacity: 0.6 }}>
          <IconShoppingCart size={64} />
        </div>
        <h2
          style={{
            fontSize: '1.75rem',
            fontWeight: 700,
            marginBottom: '0.75rem',
            color: '#1f2937',
          }}
        >
          Your Cart is Empty
        </h2>
        <p style={{ fontSize: '1.1rem' }}>
          Looks like you haven&apos;t added anything yet. Explore our collection!
        </p>
      </div>
    );
  }

  /* ---------------------------------------------------------------------- */
  /* Filled Cart                                                            */
  /* ---------------------------------------------------------------------- */
  return (
    <div className="cart-section">
      <div className="cart-items">
        {/* Header */}
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '0.75rem',
            marginBottom: '2rem',
            paddingBottom: '1rem',
            borderBottom: '2px solid #e5e7eb',
          }}
        >
          <IconShoppingCart size={28} />
          <h2
            style={{
              fontSize: '2rem',
              fontWeight: 800,
              color: '#111827',
              margin: 0,
            }}
          >
            Shopping Cart ({cart.items.length}{' '}
            {cart.items.length === 1 ? 'item' : 'items'})
          </h2>
        </div>

        {/* Items */}
        {cart.items.map(item => {
          const isBusy = updating.has(item.id);
          return (
            <div
              key={item.id}
              className="cart-item"
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '1.5rem',
                padding: '1.5rem 0',
                borderBottom: '1px solid #f3f4f6',
                opacity: isBusy ? 0.6 : 1,
                pointerEvents: isBusy ? 'none' : 'auto',
              }}
            >
              {/* Image */}
              <div
                className="cart-item-image"
                style={{
                  position: 'relative',
                  width: 90,
                  height: 90,
                  borderRadius: 12,
                  overflow: 'hidden',
                  background: '#f8fafc',
                  flexShrink: 0,
                }}
              >
                <img
                  src={item.image}
                  alt={item.name}
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                    transition: 'transform .3s ease',
                  }}
                  onMouseEnter={e => (e.currentTarget.style.transform = 'scale(1.05)')}
                  onMouseLeave={e => (e.currentTarget.style.transform = 'scale(1)')}
                  onError={e => ((e.target as HTMLImageElement).style.display = 'none')}
                />
                <div
                  style={{
                    position: 'absolute',
                    inset: 0,
                    background: 'linear-gradient(135deg, #4f46e5, #7c3aed)',
                    borderRadius: 12,
                    color: '#fff',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '1.5rem',
                    fontWeight: 700,
                  }}
                >
                  {item.name.charAt(0).toUpperCase()}
                </div>
              </div>

              {/* Details */}
              <div className="cart-item-details" style={{ flex: 1 }}>
                <div
                  style={{
                    fontSize: '1.25rem',
                    fontWeight: 700,
                    color: '#111827',
                    marginBottom: '0.35rem',
                    lineHeight: 1.3,
                  }}
                >
                  {item.name}
                </div>
                <div
                  style={{
                    color: '#4f46e5',
                    fontWeight: 700,
                    fontSize: '1.1rem',
                  }}
                >
                  ${item.price.toFixed(2)} each
                </div>
              </div>

              {/* Controls */}
              <div
                className="cart-item-controls"
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '1rem',
                }}
              >
                {/* Quantity */}
                <div
                  className="quantity-controls"
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    background: '#f3f4f6',
                    borderRadius: 50,
                    padding: '0.25rem',
                  }}
                >
                  <button
                    onClick={() => handleUpdateQuantity(item.id, item.quantity - 1)}
                    aria-label="Decrease quantity"
                    disabled={isBusy}
                    style={{
                      background: 'transparent',
                      border: 'none',
                      width: 36,
                      height: 36,
                      borderRadius: '50%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      cursor: 'pointer',
                      transition: 'background .2s',
                      color: '#374151',
                    }}
                    onMouseEnter={e => (e.currentTarget.style.background = '#e5e7eb')}
                    onMouseLeave={e => (e.currentTarget.style.background = 'transparent')}
                  >
                    <IconMinus />
                  </button>

                  <span
                    style={{
                      minWidth: 48,
                      textAlign: 'center',
                      fontWeight: 700,
                      fontSize: '1.1rem',
                      color: '#111827',
                    }}
                  >
                    {item.quantity}
                  </span>

                  <button
                    onClick={() => handleUpdateQuantity(item.id, item.quantity + 1)}
                    aria-label="Increase quantity"
                    disabled={isBusy}
                    style={{
                      background: 'transparent',
                      border: 'none',
                      width: 36,
                      height: 36,
                      borderRadius: '50%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      cursor: 'pointer',
                      transition: 'background .2s',
                      color: '#374151',
                    }}
                    onMouseEnter={e => (e.currentTarget.style.background = '#e5e7eb')}
                    onMouseLeave={e => (e.currentTarget.style.background = 'transparent')}
                  >
                    <IconPlus />
                  </button>
                </div>

                {/* Item total */}
                <div
                  style={{
                    fontWeight: 800,
                    fontSize: '1.25rem',
                    color: '#4f46e5',
                    minWidth: 90,
                    textAlign: 'center',
                  }}
                >
                  ${item.itemTotal.toFixed(2)}
                </div>

                {/* Remove */}
                <button
                  onClick={() => handleRemoveItem(item.id)}
                  aria-label="Remove item"
                  disabled={isBusy}
                  style={{
                    background: '#ef4444',
                    color: '#fff',
                    border: 'none',
                    padding: '0.65rem 1rem',
                    borderRadius: 50,
                    cursor: 'pointer',
                    fontSize: '0.9rem',
                    fontWeight: 600,
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem',
                    transition: 'all .3s ease',
                  }}
                  onMouseEnter={e => {
                    e.currentTarget.style.background = '#dc2626';
                    e.currentTarget.style.transform = 'translateY(-1px)';
                  }}
                  onMouseLeave={e => {
                    e.currentTarget.style.background = '#ef4444';
                    e.currentTarget.style.transform = 'translateY(0)';
                  }}
                >
                  <IconTrash />
                  Remove
                </button>
              </div>
            </div>
          );
        })}

        {/* Total */}
        <div
          style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginTop: '2rem',
            padding: '1.5rem 0',
            borderTop: '2px solid #e5e7eb',
            fontSize: '1.75rem',
            fontWeight: 800,
            color: '#111827',
          }}
        >
          <span>Total</span>
          <span style={{ color: '#4f46e5', fontSize: '2rem' }}>
            ${cart.total.toFixed(2)}
          </span>
        </div>

        {/* Checkout button (optional) */}
        {onProceedToCheckout && (
          <button
            onClick={onProceedToCheckout}
            style={{
              marginTop: '1.5rem',
              width: '100%',
              background: 'linear-gradient(135deg, #10b981, #059669)',
              color: '#fff',
              border: 'none',
              padding: '1rem 2rem',
              borderRadius: 50,
              fontSize: '1.1rem',
              fontWeight: 700,
              cursor: 'pointer',
              transition: 'all .3s ease',
            }}
            onMouseEnter={e => {
              e.currentTarget.style.transform = 'translateY(-2px)';
              e.currentTarget.style.boxShadow = '0 12px 25px rgba(16,185,129,.3)';
            }}
            onMouseLeave={e => {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = 'none';
            }}
          >
            Proceed to Checkout
          </button>
        )}
      </div>
    </div>
  );
};

export default Cart;
