import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Product } from '../types';
import { productAPI, cartAPI } from '../api';
import ChristmasCarousel from './ChristmasCarousel';

interface ProductGridProps {
  onCartUpdate: () => void;
}

const ProductGrid: React.FC<ProductGridProps> = ({ onCartUpdate }) => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const productsData = await productAPI.getProducts();
      setProducts(productsData);
    } catch (error) {
      console.error('Error loading products:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = async (productId: string, event: React.MouseEvent) => {
    event.stopPropagation();
    try {
      await cartAPI.addToCart(productId);
      onCartUpdate();
      // Christmas success message
      alert('üéÑ Product added to cart! Merry Shopping! üéÅ');
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('‚ùå Error adding product to cart');
    }
  };

  const handleProductClick = (productId: string) => {
    navigate(`/product/${productId}`);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading festive products... üéÑ</p>
      </div>
    );
  }

  return (
    <div className="home-page">
      {/* Christmas Carousel */}
      <ChristmasCarousel />
      
      {/* Christmas Theme Header */}
      <div className="christmas-header">
        <h2 className="festive-title">üéÖ Christmas Special Offers üéÑ</h2>
        <p className="festive-subtitle">Discover amazing deals for the holiday season!</p>
        <div className="festive-countdown">
          <span>‚è∞ Limited Time Only!</span>
        </div>
      </div>

      {/* Products Grid */}
      <div className="product-grid">
        {products.map((product: Product) => (
          <div 
            key={product.id} 
            className="product-card festive-card"
            onClick={() => handleProductClick(product.id)}
          >
            {/* Christmas Badge */}
            <div className="christmas-ribbon">üéÑ XMAS SALE</div>
            
            <div className="product-image">
              <img 
                src={product.image} 
                alt={product.name}
              />
            </div>
            
            <div className="product-info">
              <h3 className="product-name">{product.name}</h3>
              <p className="product-description">{product.description}</p>
              <div className="product-price festive-price">${product.price.toFixed(2)}</div>
              <button 
                className="add-to-cart-btn festive-cart-btn"
                onClick={(e) => handleAddToCart(product.id, e)}
              >
                üéÅ Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Christmas Footer Banner */}
      <div className="christmas-footer-banner">
        <div className="banner-content">
          <h3>üéÑ Don't Miss Out! üéÖ</h3>
          <p>Christmas delivery guaranteed for orders placed before December 20th!</p>
          <button className="banner-cta">Shop Now & Save üéÅ</button>
        </div>
      </div>
    </div>
  );
};

export default ProductGrid;
