import React from 'react';
import { Receipt as ReceiptType } from '../types';

interface ReceiptProps {
  receipt: ReceiptType;
  onClose: () => void;
}

const Receipt: React.FC<ReceiptProps> = ({ receipt, onClose }) => {
  return (
    <div className="receipt-modal">
      <div className="receipt-content">
        <div className="receipt-header">
          <h2>Order Confirmed! ðŸŽ‰</h2>
          <p>Thank you for your purchase</p>
        </div>

        <div className="receipt-details">
          <p><strong>Order ID:</strong> {receipt.orderId}</p>
          <p><strong>Date:</strong> {new Date(receipt.timestamp).toLocaleString()}</p>
          <p><strong>Customer:</strong> {receipt.customerInfo.name}</p>
          <p><strong>Email:</strong> {receipt.customerInfo.email}</p>
        </div>

        <div className="receipt-items">
          <h3>Items Purchased:</h3>
          {receipt.items.map(item => (
            <div key={item.id} className="receipt-item">
              <span>{item.name} (x{item.quantity})</span>
              <span>${item.itemTotal.toFixed(2)}</span>
            </div>
          ))}
        </div>

        <div className="receipt-total">
          Total: ${receipt.total.toFixed(2)}
        </div>

        <p className="receipt-note">
          This is a mock receipt. No actual payment was processed.
        </p>

        <button className="receipt-close-btn" onClick={onClose}>
          Continue Shopping
        </button>
      </div>
    </div>
  );
};

export default Receipt;
