import React, { useState } from 'react';
import { CustomerInfo, Receipt } from '../types';
import { checkoutAPI } from '../api';

interface CheckoutFormProps {
  onBack: () => void;
  onSuccess: (receipt: Receipt) => void;
}

const CheckoutForm: React.FC<CheckoutFormProps> = ({ onBack, onSuccess }) => {
  const [customerInfo, setCustomerInfo] = useState<CustomerInfo>({
    name: '',
    email: ''
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const receipt = await checkoutAPI.checkout(customerInfo);
      onSuccess(receipt);
    } catch (error) {
      console.error('Checkout error:', error);
      alert('Error during checkout. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setCustomerInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <form className="checkout-form" onSubmit={handleSubmit}>
      <h2>Checkout</h2>
      <p>Please enter your information to complete the purchase</p>
      
      <div className="form-group">
        <label htmlFor="name">Full Name</label>
        <input
          type="text"
          id="name"
          name="name"
          value={customerInfo.name}
          onChange={handleChange}
          required
          placeholder="Enter your full name"
        />
      </div>

      <div className="form-group">
        <label htmlFor="email">Email Address</label>
        <input
          type="email"
          id="email"
          name="email"
          value={customerInfo.email}
          onChange={handleChange}
          required
          placeholder="Enter your email"
        />
      </div>

      <div className="form-actions">
        <button type="button" className="back-btn" onClick={onBack}>
          Back to Cart
        </button>
        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? 'Processing...' : 'Complete Purchase'}
        </button>
      </div>
    </form>
  );
};

export default CheckoutForm;
