import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import ProductGrid from './components/ProductGrid';
import ProductDetails from './components/ProductDetails';
import Cart from './components/Cart';
import CheckoutForm from './components/CheckoutForm';
import Receipt from './components/Receipt';
import { Cart as CartType, Receipt as ReceiptType } from './types';
import './App.css';
import GlobalSnow from './components/GlobalSnow';
// Header component to handle navigation properly
const Header: React.FC<{ cart: CartType }> = ({ cart }) => {
  const navigate = useNavigate();

  return (
    <header className="app-header">
      <div className="container">
        <h1 onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>
          üõçÔ∏è Vibe Commerce
        </h1>
        <nav className="app-nav">
          <button 
            className="nav-btn cart-nav-btn"
            onClick={() => navigate('/cart')}
          >
            üõí Cart
            {cart.items.length > 0 && (
              <span className="cart-count">
                {cart.items.reduce((total, item) => total + item.quantity, 0)}
              </span>
            )}
          </button>
        </nav>
      </div>
    </header>
  );
};

// Cart Section component
const CartSection: React.FC<{ cart: CartType; onCartUpdate: () => void }> = ({ cart, onCartUpdate }) => {
  const navigate = useNavigate();

  return (
    <div className="cart-section">
      <Cart cart={cart} onCartUpdate={onCartUpdate} />
      {cart.items.length > 0 && (
        <button 
          className="checkout-btn"
          onClick={() => navigate('/checkout')}
        >
          Proceed to Checkout
        </button>
      )}
    </div>
  );
};

const App: React.FC = () => {
  const [cart, setCart] = useState<CartType>({ items: [], total: 0 });
  const [receipt, setReceipt] = useState<ReceiptType | null>(null);

  const refreshCart = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/cart');
      const cartData = await response.json();
      setCart(cartData);
    } catch (error) {
      console.error('Error fetching cart:', error);
    }
  };

  return (
    <Router>
      <div className="App">
        <Header cart={cart} />

        <main className="app-main">
          <div className="container">
            <Routes>
              <Route path="/" element={<ProductGrid onCartUpdate={refreshCart} />} />
              <Route path="/product/:productId" element={<ProductDetails onCartUpdate={refreshCart} />} />
              <Route path="/cart" element={<CartSection cart={cart} onCartUpdate={refreshCart} />} />
              <Route path="/checkout" element={
                <CheckoutForm 
                  onBack={() => window.history.back()}
                  onSuccess={(receipt) => {
                    setReceipt(receipt);
                    refreshCart();
                  }}
                />
              } />
            </Routes>
          </div>
        </main>

        {receipt && (
          <Receipt 
            receipt={receipt}
            onClose={() => {
              setReceipt(null);
              // Use proper navigation instead of window.location
              const navigate = (window as any).navigate || (() => window.location.href = '/');
              navigate('/');
            }}
          />
        )}
        <GlobalSnow />
      </div>
    </Router>
  );
};

export default App;
