import React from 'react';
import { Cart as CartType } from '../types';
import { cartAPI } from '../api';
import { ShoppingCart, Trash2, Plus, Minus } from 'lucide-react';

interface CartProps {
  cart: CartType;
  onCartUpdate: () => void;
}

const Cart: React.FC<CartProps> = ({ cart, onCartUpdate }) => {
  const handleRemoveItem = async (cartItemId: string) => {
    try {
      await cartAPI.removeFromCart(cartItemId);
      onCartUpdate();
    } catch (error) {
      console.error('Error removing item:', error);
      alert('Failed to remove item. Please try again.');
    }
  };

  const handleUpdateQuantity = async (cartItemId: string, newQuantity: number) => {
    if (newQuantity < 1) {
      handleRemoveItem(cartItemId);
      return;
    }

    try {
      const item = cart.items.find((i) => i.id === cartItemId);
      if (!item) return;

      // Optimistically update UI
      const originalQuantity = item.quantity;
      item.quantity = newQuantity;
      item.itemTotal = item.price * newQuantity;
      cart.total = cart.items.reduce((sum, i) => sum + i.itemTotal, 0);

      // API calls
      await cartAPI.removeFromCart(cartItemId);
      await cartAPI.addToCart(item.productId, newQuantity);

      onCartUpdate();
    } catch (error) {
      console.error('Error updating quantity:', error);
      alert('Failed to update quantity. Please try again.');
      onCartUpdate(); // Re-fetch to restore correct state
    }
  };

  if (cart.items.length === 0) {
    return (
      <div className="cart-empty" style={{ textAlign: 'center', padding: '4rem 2rem' }}>
        <div style={{ marginBottom: '1.5rem', opacity: 0.6 }}>
          <ShoppingCart size={64} strokeWidth={1.5} />
        </div>
        <h2 style={{ fontSize: '1.75rem', fontWeight: 700, marginBottom: '0.75rem', color: '#1f2937' }}>
          Your Cart is Empty
        </h2>
        <p style={{ color: '#6b7280', fontSize: '1.1rem' }}>
          Looks like you haven&apos;t added anything yet. Explore our collection!
        </p>
      </div>
    );
  }

  return (
    <div className="cart-section">
      <div className="cart-items">
        <div className="cart-header" style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '0.75rem',
          marginBottom: '2rem',
          paddingBottom: '1rem',
          borderBottom: '2px solid #e5e7eb'
        }}>
          <ShoppingCart size={28} />
          <h2 style={{ 
            fontSize: '2rem', 
            fontWeight: 800, 
            color: '#111827',
            margin: 0
          }}>
            Shopping Cart ({cart.items.length} {cart.items.length === 1 ? 'item' : 'items'})
          </h2>
        </div>

        {cart.items.map((item) => (
          <div key={item.id} className="cart-item">
            <div className="cart-item-image">
              <img
                src={item.image}
                alt={item.name}
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'cover',
                  borderRadius: '12px',
                  transition: 'transform 0.3s ease',
                }}
                onMouseEnter={(e) => (e.currentTarget.style.transform = 'scale(1.05)')}
                onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
                onError={(e) => {
                  (e.target as HTMLImageElement).style.display = 'none';
                }}
              />
              <div
                className="image-fallback"
                style={{
                  position: 'absolute',
                  inset: 0,
                  background: 'linear-gradient(135deg, #4f46e5, #7c3aed)',
                  borderRadius: '12px',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '1.5rem',
                  fontWeight: 700,
                  textShadow: '0 1px 2px rgba(0,0,0,0.2)',
                }}
              >
                {item.name.charAt(0).toUpperCase()}
              </div>
            </div>

            <div className="cart-item-details">
              <div
                className="cart-item-name"
                style={{
                  fontSize: '1.25rem',
                  fontWeight: 700,
                  color: '#111827',
                  marginBottom: '0.35rem',
                  lineHeight: 1.3,
                }}
              >
                {item.name}
              </div>
              <div
                className="cart-item-price"
                style={{
                  color: '#4f46e5',
                  fontWeight: 700,
                  fontSize: '1.1rem',
                }}
              >
                ${item.price.toFixed(2)} each
              </div>
            </div>

            <div className="cart-item-controls">
              <div className="quantity-controls">
                <button
                  className="quantity-btn"
                  onClick={() => handleUpdateQuantity(item.id, item.quantity - 1)}
                  aria-label="Decrease quantity"
                  style={{
                    background: '#f3f4f6',
                    border: 'none',
                    width: '40px',
                    height: '40px',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease',
                    fontWeight: 700,
                    color: '#374151',
                  }}
                  onMouseEnter={(e) => (e.currentTarget.style.background = '#e5e7eb')}
                  onMouseLeave={(e) => (e.currentTarget.style.background = '#f3f4f6')}
                >
                  <Minus size={16} />
                </button>

                <span
                  className="quantity-display"
                  style={{
                    minWidth: '48px',
                    textAlign: 'center',
                    fontWeight: 700,
                    fontSize: '1.1rem',
                    color: '#111827',
                  }}
                >
                  {item.quantity}
                </span>

                <button
                  className="quantity-btn"
                  onClick={() => handleUpdateQuantity(item.id, item.quantity + 1)}
                  aria-label="Increase quantity"
                  style={{
                    background: '#f3f4f6',
                    border: 'none',
                    width: '40px',
                    height: '40px',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease',
                    fontWeight: 700,
                    color: '#374151',
                  }}
                  onMouseEnter={(e) => (e.currentTarget.style.background = '#e5e7eb')}
                  onMouseLeave={(e) => (e.currentTarget.style.background = '#f3f4f6')}
                >
                  <Plus size={16} />
                </button>
              </div>

              <div
                className="item-total"
                style={{
                  fontWeight: 800,
                  fontSize: '1.25rem',
                  color: '#4f46e5',
                  minWidth: '90px',
                  textAlign: 'center',
                }}
              >
                ${item.itemTotal.toFixed(2)}
              </div>

              <button
                className="remove-btn"
                onClick={() => handleRemoveItem(item.id)}
                aria-label="Remove item"
                style={{
                  background: '#ef4444',
                  color: 'white',
                  border: 'none',
                  padding: '0.65rem 1rem',
                  borderRadius: '50px',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: 600,
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  transition: 'all 0.3s ease',
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.background = '#dc2626';
                  e.currentTarget.style.transform = 'translateY(-1px)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.background = '#ef4444';
                  e.currentTarget.style.transform = 'translateY(0)';
                }}
              >
                <Trash2 size={16} />
                Remove
              </button>
            </div>
          </div>
        ))}

        <div
          className="cart-total"
          style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginTop: '2rem',
            padding: '1.5rem 0',
            borderTop: '2px solid #e5e7eb',
            fontSize: '1.75rem',
            fontWeight: 800,
            color: '#111827',
          }}
        >
          <span>Total</span>
          <span style={{ color: '#4f46e5', fontSize: '2rem' }}>
            ${cart.total.toFixed(2)}
          </span>
        </div>
      </div>
    </div>
  );
};

export default Cart;
