import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ProductGrid from './components/ProductGrid';
import ProductDetails from './components/ProductDetails';
import Cart from './components/Cart';
import CheckoutForm from './components/CheckoutForm';
import Receipt from './components/Receipt';
import { Cart as CartType, Receipt as ReceiptType } from './types';
import './App.css';

const App: React.FC = () => {
  const [cart, setCart] = useState<CartType>({ items: [], total: 0 });
  const [receipt, setReceipt] = useState<ReceiptType | null>(null);

  const refreshCart = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/cart');
      const cartData = await response.json();
      setCart(cartData);
    } catch (error) {
      console.error('Error fetching cart:', error);
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="app-header">
          <h1 onClick={() => window.location.href = '/'} style={{ cursor: 'pointer' }}>
            üõçÔ∏è Vibe Commerce
          </h1>
          <nav className="app-nav">
            <button 
              onClick={() => window.location.href = '/cart'}
              className="cart-nav-btn"
            >
              üõí Cart ({cart.items.reduce((total, item) => total + item.quantity, 0)})
            </button>
          </nav>
        </header>

        <main className="app-main">
          <Routes>
            <Route path="/" element={<ProductGrid onCartUpdate={refreshCart} />} />
            <Route path="/product/:productId" element={<ProductDetails onCartUpdate={refreshCart} />} />
            <Route path="/cart" element={
              <div className="cart-section">
                <Cart cart={cart} onCartUpdate={refreshCart} />
                {cart.items.length > 0 && (
                  <button 
                    className="checkout-btn"
                    onClick={() => window.location.href = '/checkout'}
                  >
                    Proceed to Checkout
                  </button>
                )}
              </div>
            } />
            <Route path="/checkout" element={
              <CheckoutForm 
                onBack={() => window.location.href = '/cart'}
                onSuccess={(receipt) => {
                  setReceipt(receipt);
                  refreshCart();
                }}
              />
            } />
          </Routes>
        </main>

        {receipt && (
          <Receipt 
            receipt={receipt}
            onClose={() => {
              setReceipt(null);
              window.location.href = '/';
            }}
          />
        )}
      </div>
    </Router>
  );
};

export default App;
