import React, { useState } from 'react';
import ProductGrid from './components/ProductGrid';
import Cart from './components/Cart';
import CheckoutForm from './components/CheckoutForm';
import Receipt from './components/Receipt';
import { Cart as CartType, Receipt as ReceiptType } from './types';
import './App.css';

const App: React.FC = () => {
  const [activeView, setActiveView] = useState<'products' | 'cart' | 'checkout'>('products');
  const [cart, setCart] = useState<CartType>({ items: [], total: 0 });
  const [receipt, setReceipt] = useState<ReceiptType | null>(null);

  const refreshCart = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/cart');
      const cartData = await response.json();
      setCart(cartData);
    } catch (error) {
      console.error('Error fetching cart:', error);
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Vibe Commerce</h1>
        <nav className="app-nav">
          <button 
            onClick={() => setActiveView('products')}
            className={activeView === 'products' ? 'active' : ''}
          >
            Products
          </button>
          <button 
            onClick={() => setActiveView('cart')}
            className={activeView === 'cart' ? 'active' : ''}
          >
            Cart ({cart.items.reduce((total, item) => total + item.quantity, 0)})
          </button>
        </nav>
      </header>

      <main className="app-main">
        {activeView === 'products' && (
          <ProductGrid onCartUpdate={refreshCart} />
        )}

        {activeView === 'cart' && (
          <div className="cart-section">
            <Cart cart={cart} onCartUpdate={refreshCart} />
            {cart.items.length > 0 && (
              <button 
                className="checkout-btn"
                onClick={() => setActiveView('checkout')}
              >
                Proceed to Checkout
              </button>
            )}
          </div>
        )}

        {activeView === 'checkout' && (
          <CheckoutForm 
            onBack={() => setActiveView('cart')}
            onSuccess={(receipt) => {
              setReceipt(receipt);
              refreshCart();
            }}
          />
        )}
      </main>

      {receipt && (
        <Receipt 
          receipt={receipt}
          onClose={() => {
            setReceipt(null);
            setActiveView('products');
          }}
        />
      )}
    </div>
  );
};

export default App;
