import React, { useState } from 'react';
import { CustomerInfo, Receipt } from '../types';
import { checkoutAPI } from '../api';

interface CheckoutFormProps {
  onBack: () => void;
  onSuccess: (receipt: Receipt) => void;
}

const CheckoutForm: React.FC<CheckoutFormProps> = ({ onBack, onSuccess }) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [customerInfo, setCustomerInfo] = useState<CustomerInfo>({
    name: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    zipCode: ''
  });
  const [paymentInfo, setPaymentInfo] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardName: ''
  });

  const totalSteps = 3;

  const handleCustomerInfoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setCustomerInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePaymentInfoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setPaymentInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const receipt = await checkoutAPI.checkout(customerInfo);
      onSuccess(receipt);
    } catch (error) {
      console.error('Checkout error:', error);
      alert('Error during checkout. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const nextStep = () => {
    if (currentStep < totalSteps) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const isStepValid = () => {
    switch (currentStep) {
      case 1:
        return customerInfo.name.trim() && customerInfo.email.trim() && customerInfo.phone.trim();
      case 2:
        return customerInfo.address.trim() && customerInfo.city.trim() && customerInfo.zipCode.trim();
      case 3:
        return paymentInfo.cardNumber.replace(/\s/g, '').length === 16 &&
               paymentInfo.expiryDate.length === 5 &&
               paymentInfo.cvv.length === 3 &&
               paymentInfo.cardName.trim();
      default:
        return false;
    }
  };

  // Format card number with spaces
  const formatCardNumber = (value: string) => {
    const v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '');
    const matches = v.match(/\d{4,16}/g);
    const match = matches && matches[0] || '';
    const parts = [];
    
    for (let i = 0, len = match.length; i < len; i += 4) {
      parts.push(match.substring(i, i + 4));
    }
    
    return parts.length ? parts.join(' ') : value;
  };

  // Format expiry date
  const formatExpiryDate = (value: string) => {
    const v = value.replace(/\D/g, '').substring(0, 4);
    if (v.length >= 2) {
      return `${v.substring(0, 2)}/${v.substring(2, 4)}`;
    }
    return v;
  };

  const renderStepIndicator = () => (
    <div className="checkout-steps">
      {[1, 2, 3].map(step => (
        <div key={step} className={`step ${step === currentStep ? 'active' : ''} ${step < currentStep ? 'completed' : ''}`}>
          <div className="step-number">
            {step < currentStep ? '‚úì' : step}
          </div>
          <div className="step-label">
            {step === 1 && 'Personal Info'}
            {step === 2 && 'Shipping Address'}
            {step === 3 && 'Payment'}
          </div>
        </div>
      ))}
      <div className="step-connector"></div>
    </div>
  );

  const renderStep1 = () => (
    <div className="checkout-step active">
      <div className="step-header">
        <h3>üéÖ Personal Information</h3>
        <p>Let us know how to reach you</p>
      </div>
      
      <div className="form-grid">
        <div className="form-group">
          <label htmlFor="name">Full Name *</label>
          <input
            type="text"
            id="name"
            name="name"
            value={customerInfo.name}
            onChange={handleCustomerInfoChange}
            required
            placeholder="Enter your full name"
            className="christmas-input"
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email Address *</label>
          <input
            type="email"
            id="email"
            name="email"
            value={customerInfo.email}
            onChange={handleCustomerInfoChange}
            required
            placeholder="Enter your email"
            className="christmas-input"
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone Number *</label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={customerInfo.phone}
            onChange={handleCustomerInfoChange}
            required
            placeholder="Enter your phone number"
            className="christmas-input"
          />
        </div>
      </div>
    </div>
  );

  const renderStep2 = () => (
    <div className="checkout-step active">
      <div className="step-header">
        <h3>üöö Shipping Address</h3>
        <p>Where should we deliver your Christmas gifts?</p>
      </div>
      
      <div className="form-grid">
        <div className="form-group full-width">
          <label htmlFor="address">Street Address *</label>
          <input
            type="text"
            id="address"
            name="address"
            value={customerInfo.address}
            onChange={handleCustomerInfoChange}
            required
            placeholder="Enter your street address"
            className="christmas-input"
          />
        </div>

        <div className="form-group">
          <label htmlFor="city">City *</label>
          <input
            type="text"
            id="city"
            name="city"
            value={customerInfo.city}
            onChange={handleCustomerInfoChange}
            required
            placeholder="Enter your city"
            className="christmas-input"
          />
        </div>

        <div className="form-group">
          <label htmlFor="zipCode">ZIP Code *</label>
          <input
            type="text"
            id="zipCode"
            name="zipCode"
            value={customerInfo.zipCode}
            onChange={handleCustomerInfoChange}
            required
            placeholder="Enter ZIP code"
            className="christmas-input"
          />
        </div>
      </div>
    </div>
  );

  const renderStep3 = () => (
    <div className="checkout-step active">
      <div className="step-header">
        <h3>üí≥ Payment Information</h3>
        <p>Secure payment with 256-bit encryption</p>
      </div>
      
      <div className="payment-card">
        <div className="card-header">
          <span className="card-chip">üí≥</span>
          <span className="card-type">VISA</span>
        </div>
        
        <div className="form-grid">
          <div className="form-group full-width">
            <label htmlFor="cardNumber">Card Number *</label>
            <input
              type="text"
              id="cardNumber"
              name="cardNumber"
              value={paymentInfo.cardNumber}
              onChange={(e) => {
                const formatted = formatCardNumber(e.target.value);
                setPaymentInfo(prev => ({ ...prev, cardNumber: formatted }));
              }}
              required
              placeholder="1234 5678 9012 3456"
              maxLength={19}
              className="christmas-input"
            />
          </div>

          <div className="form-group">
            <label htmlFor="cardName">Name on Card *</label>
            <input
              type="text"
              id="cardName"
              name="cardName"
              value={paymentInfo.cardName}
              onChange={handlePaymentInfoChange}
              required
              placeholder="Enter name on card"
              className="christmas-input"
            />
          </div>

          <div className="form-group">
            <label htmlFor="expiryDate">Expiry Date *</label>
            <input
              type="text"
              id="expiryDate"
              name="expiryDate"
              value={paymentInfo.expiryDate}
              onChange={(e) => {
                const formatted = formatExpiryDate(e.target.value);
                setPaymentInfo(prev => ({ ...prev, expiryDate: formatted }));
              }}
              required
              placeholder="MM/YY"
              maxLength={5}
              className="christmas-input"
            />
          </div>

          <div className="form-group">
            <label htmlFor="cvv">CVV *</label>
            <input
              type="text"
              id="cvv"
              name="cvv"
              value={paymentInfo.cvv}
              onChange={handlePaymentInfoChange}
              required
              placeholder="123"
              maxLength={3}
              className="christmas-input"
            />
          </div>
        </div>
      </div>

      <div className="security-notice">
        <div className="security-icon">üîí</div>
        <div className="security-text">
          <strong>Your payment is secure</strong>
          <span>All transactions are encrypted and secure</span>
        </div>
      </div>
    </div>
  );

  return (
    <div className="checkout-wizard">
      {/* Christmas Header */}
      <div className="checkout-header">
        <h1>üéÑ Complete Your Christmas Order</h1>
        <p>Just a few steps to get your festive gifts delivered!</p>
      </div>

      {/* Step Indicator */}
      {renderStepIndicator()}

      {/* Form Content */}
      <form className="checkout-form-modern" onSubmit={handleSubmit}>
        {currentStep === 1 && renderStep1()}
        {currentStep === 2 && renderStep2()}
        {currentStep === 3 && renderStep3()}

        {/* Navigation Buttons */}
        <div className="checkout-actions">
          {currentStep > 1 && (
            <button 
              type="button" 
              className="nav-btn prev-btn"
              onClick={prevStep}
            >
              ‚Üê Previous
            </button>
          )}
          
          {currentStep === 1 && (
            <button 
              type="button" 
              className="back-to-cart-btn"
              onClick={onBack}
            >
              üõí Back to Cart
            </button>
          )}

          <div className="nav-right">
            {currentStep < totalSteps ? (
              <button 
                type="button" 
                className="nav-btn next-btn"
                onClick={nextStep}
                disabled={!isStepValid()}
              >
                Next Step ‚Üí
              </button>
            ) : (
              <button 
                type="submit" 
                className="submit-btn complete-purchase"
                disabled={!isStepValid() || loading}
              >
                {loading ? (
                  <>
                    <div className="loading-spinner-small"></div>
                    Processing...
                  </>
                ) : (
                  <>
                    üéÅ Complete Purchase
                  </>
                )}
              </button>
            )}
          </div>
        </div>
      </form>

      {/* Christmas Offers Sidebar */}
      <div className="checkout-sidebar">
        <div className="sidebar-card">
          <h4>üéÖ Christmas Special Offers</h4>
          <ul className="offer-list">
            <li>‚úÖ Free gift wrapping</li>
            <li>‚úÖ Express delivery available</li>
            <li>‚úÖ 30-day return policy</li>
            <li>‚úÖ Free shipping over $50</li>
          </ul>
        </div>

        <div className="sidebar-card">
          <h4>üì¶ Delivery Info</h4>
          <div className="delivery-estimate">
            <div className="delivery-date">
              <span className="date-icon">üìÖ</span>
              <span>Est. Delivery: Dec 24-26</span>
            </div>
            <small>Order within 12 hours for Christmas delivery</small>
          </div>
        </div>

        <div className="sidebar-card support-card">
          <h4>üõü Need Help?</h4>
          <p>Our Christmas support elves are here to help!</p>
          <button className="support-btn">Contact Support</button>
        </div>
      </div>
    </div>
  );
};

export default CheckoutForm;
