// App.tsx
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import ProductGrid from './components/ProductGrid';
import ProductDetails from './components/ProductDetails';
import Cart from './components/Cart';
import CheckoutForm from './components/CheckoutForm';
import Receipt from './components/Receipt';
import ChristmasCarousel from './components/ChristmasCarousel';
import GlobalSnow from './components/GlobalSnow';
import { Cart as CartType, Receipt as ReceiptType } from './types';
import './App.css';

const Header: React.FC<{ cart: CartType }> = ({ cart }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const totalItems = cart.items.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <header className="app-header">
      <div className="container header-content">
        <h1
          onClick={() => navigate('/')}
          className="festive-title-header"
          style={{ cursor: 'pointer' }}
        >
          Vibe Commerce
        </h1>
        <nav className="app-nav">
          <button
            className={`nav-btn ${location.pathname === '/' ? 'active' : ''}`}
            onClick={() => navigate('/')}
          >
            Home
          </button>
          <button
            className={`nav-btn cart-nav-btn ${location.pathname === '/cart' ? 'active' : ''}`}
            onClick={() => navigate('/cart')}
          >
            Cart
            {totalItems > 0 && (
              <span className="cart-count festive-cart-count">{totalItems}</span>
            )}
          </button>
        </nav>
      </div>
    </header>
  );
};

const CartSection: React.FC<{ cart: CartType; onCartUpdate: () => void }> = ({ cart, onCartUpdate }) => {
  const navigate = useNavigate();

  return (
    <div className="cart-section">
      <Cart cart={cart} onCartUpdate={onCartUpdate} />
      {cart.items.length > 0 && (
        <button
          className="checkout-btn"
          onClick={() => navigate('/checkout')}
        >
          Proceed to Checkout
        </button>
      )}
    </div>
  );
};

const App: React.FC = () => {
  const [cart, setCart] = useState<CartType>({ items: [], total: 0 });
  const [receipt, setReceipt] = useState<ReceiptType | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  const refreshCart = async () => {
    try {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/api/cart');
      if (!response.ok) throw new Error('Failed to fetch cart');
      const cartData = await response.json();
      setCart(cartData);
    } catch (error) {
      console.error('Error fetching cart:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    refreshCart();
  }, []);

  return (
    <Router>
      <div className="App home-page">
        {/* Global Snow & Stars */}
        <GlobalSnow />

        {/* Header */}
        <Header cart={cart} />

        {/* Main Content */}
        <main className="app-main">
          <div className="container">
            <Routes>
              <Route
                path="/"
                element={
                  <>
                    {/* Festive Carousel */}
                    <ChristmasCarousel />
                    {/* Christmas Header */}
                    <div className="christmas-header">
                      <h1 className="festive-title">Merry Christmas Sale!</h1>
                      <p className="festive-subtitle">
                        Up to <strong>60% OFF</strong> on all gifts • Free Shipping • Limited Time
                      </p>
                    </div>
                    {/* Product Grid */}
                    <ProductGrid onCartUpdate={refreshCart} />
                  </>
                }
              />
              <Route
                path="/product/:productId"
                element={<ProductDetails onCartUpdate={refreshCart} />}
              />
              <Route
                path="/cart"
                element={<CartSection cart={cart} onCartUpdate={refreshCart} />}
              />
              <Route
                path="/checkout"
                element={
                  <CheckoutForm
                    onBack={() => window.history.back()}
                    onSuccess={(receipt) => {
                      setReceipt(receipt);
                      refreshCart();
                    }}
                  />
                }
              />
            </Routes>
          </div>
        </main>

        {/* Receipt Modal with Christmas Confetti */}
        {receipt && (
          <div className="receipt-modal">
            <div className="receipt-content">
              <Receipt
                receipt={receipt}
                onClose={() => {
                  setReceipt(null);
                  // Navigate home after closing
                  const navigate = (window as any).navigate || (() => window.location.href = '/');
                  navigate('/');
                }}
              />
              {/* Christmas Confetti */}
              <div className="confetti-layer">
                {Array.from({ length: 50 }).map((_, i) => (
                  <div
                    key={i}
                    className="confetti"
                    style={{
                      left: `${Math.random() * 100}%`,
                      animationDelay: `${Math.random() * 2}s`,
                      backgroundColor: ['#dc2626', '#10b981', '#fbbf24', '#7c3aed'][Math.floor(Math.random() * 4)],
                    } as React.CSSProperties}
                  />
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </Router>
  );
};

export default App;
