import React, { useState, useEffect } from 'react';
import './ChristmasCarousel.css';

const ChristmasCarousel: React.FC = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const offers = [
    {
      id: 1,
      title: "Christmas Special",
      subtitle: "Up to 50% OFF",
      description: "Get amazing discounts on all electronics this holiday season!",
      image: "üéÑ",
      backgroundColor: "#dc2626",
      textColor: "#ffffff"
    },
    {
      id: 2,
      title: "Festive Bundle",
      subtitle: "Buy 2 Get 1 Free",
      description: "Perfect gifts for your loved ones with our special bundles",
      image: "üéÅ",
      backgroundColor: "#059669",
      textColor: "#ffffff"
    },
    {
      id: 3,
      title: "New Year Sale",
      subtitle: "Limited Time Offer",
      description: "Start the new year with the latest tech at unbeatable prices",
      image: "‚≠ê",
      backgroundColor: "#7c3aed",
      textColor: "#ffffff"
    },
    {
      id: 4,
      title: "Free Shipping",
      subtitle: "On All Orders",
      description: "Free delivery worldwide for orders above $50. Limited time!",
      image: "üöö",
      backgroundColor: "#0369a1",
      textColor: "#ffffff"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % offers.length);
    }, 4000);

    return () => clearInterval(timer);
  }, [offers.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % offers.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + offers.length) % offers.length);
  };

  return (
    <div className="christmas-carousel">
      {/* Falling Snow Animation */}
      <div className="snowfall">
        {Array.from({ length: 50 }).map((_, i) => (
          <div key={i} className="snowflake">‚ùÑ</div>
        ))}
      </div>

      {/* Falling Stars Animation */}
      <div className="starfall">
        {Array.from({ length: 20 }).map((_, i) => (
          <div key={i} className="star">‚≠ê</div>
        ))}
      </div>

      <div className="carousel-container">
        <button className="carousel-btn prev" onClick={prevSlide}>‚Äπ</button>
        
        <div className="carousel-track">
          {offers.map((offer, index) => (
            <div
              key={offer.id}
              className={`carousel-slide ${index === currentSlide ? 'active' : ''}`}
              style={{
                transform: `translateX(${(index - currentSlide) * 100}%)`,
                background: offer.backgroundColor,
                color: offer.textColor
              }}
            >
              <div className="slide-content">
                <div className="offer-emoji">{offer.image}</div>
                <h2 className="offer-title">{offer.title}</h2>
                <h3 className="offer-subtitle">{offer.subtitle}</h3>
                <p className="offer-description">{offer.description}</p>
                <button className="shop-now-btn">Shop Now</button>
              </div>
            </div>
          ))}
        </div>

        <button className="carousel-btn next" onClick={nextSlide}>‚Ä∫</button>
      </div>

      <div className="carousel-indicators">
        {offers.map((_, index) => (
          <button
            key={index}
            className={`indicator ${index === currentSlide ? 'active' : ''}`}
            onClick={() => setCurrentSlide(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default ChristmasCarousel;
