import React from 'react';
import { Cart as CartType } from '../types';
import { cartAPI } from '../api';

interface CartProps {
  cart: CartType;
  onCartUpdate: () => void;
}

const Cart: React.FC<CartProps> = ({ cart, onCartUpdate }) => {
  const handleRemoveItem = async (cartItemId: string) => {
    try {
      await cartAPI.removeFromCart(cartItemId);
      onCartUpdate();
    } catch (error) {
      console.error('Error removing item:', error);
      alert('Error removing item from cart');
    }
  };

  const handleUpdateQuantity = async (cartItemId: string, newQuantity: number) => {
    if (newQuantity < 1) {
      handleRemoveItem(cartItemId);
      return;
    }

    try {
      const item = cart.items.find(item => item.id === cartItemId);
      if (item) {
        await cartAPI.removeFromCart(cartItemId);
        await cartAPI.addToCart(item.productId, newQuantity);
        onCartUpdate();
      }
    } catch (error) {
      console.error('Error updating quantity:', error);
      alert('Error updating quantity');
    }
  };

  if (cart.items.length === 0) {
    return (
      <div className="cart-empty" style={{ textAlign: 'center', padding: '3rem' }}>
        <h2>Your Cart is Empty</h2>
        <p>Add some products to get started!</p>
      </div>
    );
  }

  return (
    <div className="cart-items">
      <h2>Shopping Cart</h2>
      {cart.items.map(item => (
        <div key={item.id} className="cart-item">
          <div className="cart-item-image">
            <img 
              src={item.image} 
              alt={item.name}
              style={{ 
                width: '100%', 
                height: '100%', 
                objectFit: 'cover',
                borderRadius: '8px'
              }}
              onError={(e) => {
                (e.target as HTMLImageElement).style.display = 'none';
              }}
            />
            <div 
              className="image-fallback"
              style={{
                display: 'none',
                width: '100%',
                height: '100%',
                background: 'linear-gradient(45deg, #667eea, #764ba2)',
                borderRadius: '8px',
                color: 'white',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '1.2rem'
              }}
            >
              {item.name.charAt(0)}
            </div>
          </div>
          <div className="cart-item-details">
            <div className="cart-item-name">{item.name}</div>
            <div className="cart-item-price">${item.price.toFixed(2)} each</div>
          </div>
          <div className="cart-item-controls">
            <div className="quantity-controls">
              <button 
                className="quantity-btn"
                onClick={() => handleUpdateQuantity(item.id, item.quantity - 1)}
              >
                -
              </button>
              <span className="quantity" style={{ padding: '0 1rem' }}>{item.quantity}</span>
              <button 
                className="quantity-btn"
                onClick={() => handleUpdateQuantity(item.id, item.quantity + 1)}
              >
                +
              </button>
            </div>
            <div className="item-total" style={{ fontWeight: '600', minWidth: '80px' }}>
              ${item.itemTotal.toFixed(2)}
            </div>
            <button 
              className="remove-btn"
              onClick={() => handleRemoveItem(item.id)}
            >
              Remove
            </button>
          </div>
        </div>
      ))}
      <div className="cart-total">
        Total: ${cart.total.toFixed(2)}
      </div>
    </div>
  );
};

export default Cart;
