import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Product } from '../types';
import { productAPI, cartAPI } from '../api';

const ProductDetails: React.FC<{ onCartUpdate: () => void }> = ({ onCartUpdate }) => {
  const { productId } = useParams<{ productId: string }>();
  const navigate = useNavigate();
  const [product, setProduct] = useState<Product | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (productId) {
      loadProduct();
    }
  }, [productId]);

  const loadProduct = async () => {
    try {
      const productData = await productAPI.getProduct(productId!);
      setProduct(productData);
    } catch (error) {
      console.error('Error loading product:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = async () => {
    if (!product) return;
    
    try {
      await cartAPI.addToCart(product.id);
      onCartUpdate();
      alert('Product added to cart!');
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Error adding product to cart');
    }
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, index) => (
      <span key={index} style={{ color: index < Math.floor(rating) ? '#ffc107' : '#e4e5e9' }}>
        ★
      </span>
    ));
  };

  if (loading) {
    return <div className="loading" style={{ textAlign: 'center', padding: '2rem' }}>Loading product details...</div>;
  }

  if (!product) {
    return (
      <div className="product-not-found" style={{ textAlign: 'center', padding: '3rem' }}>
        <h2>Product Not Found</h2>
        <button 
          onClick={() => navigate('/')}
          style={{
            background: '#667eea',
            color: 'white',
            border: 'none',
            padding: '0.75rem 1.5rem',
            borderRadius: '8px',
            cursor: 'pointer',
            marginTop: '1rem'
          }}
        >
          Back to Products
        </button>
      </div>
    );
  }

  return (
    <div className="product-details">
      <button 
        className="back-button"
        onClick={() => navigate(-1)}
        style={{
          background: 'none',
          border: 'none',
          color: '#667eea',
          cursor: 'pointer',
          fontSize: '1rem',
          marginBottom: '1rem',
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem'
        }}
      >
        ← Back to Products
      </button>

      <div className="product-details-content">
        {/* Product Images */}
        <div className="product-images">
          <div className="main-image">
            <img 
              src={product.image} 
              alt={product.name}
              style={{
                width: '100%',
                height: '400px',
                objectFit: 'cover',
                borderRadius: '12px'
              }}
            />
          </div>
        </div>

        {/* Product Info */}
        <div className="product-info">
          <div className="product-header">
            <h1 className="product-title">{product.name}</h1>
            <div className="product-rating">
              <div className="stars">
                {renderStars(product.rating)}
                <span className="rating-text">({product.reviewCount} reviews)</span>
              </div>
            </div>
            <div className="product-brand">Brand: {product.brand}</div>
            <div className="product-price">${product.price.toFixed(2)}</div>
            <div className={`stock-status ${product.inStock ? 'in-stock' : 'out-of-stock'}`}>
              {product.inStock ? `In Stock (${product.stockQuantity} available)` : 'Out of Stock'}
            </div>
          </div>

          <div className="product-actions">
            <button 
              className="add-to-cart-btn large"
              onClick={handleAddToCart}
              disabled={!product.inStock}
            >
              {product.inStock ? 'Add to Cart' : 'Out of Stock'}
            </button>
            <button className="buy-now-btn">
              Buy Now
            </button>
          </div>

          {/* Product Features */}
          <div className="product-section">
            <h3>Key Features</h3>
            <ul className="features-list">
              {product.features.map((feature: string, index: number) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
          </div>

          {/* Specifications */}
          <div className="product-section">
            <h3>Specifications</h3>
            <div className="specifications">
              {Object.entries(product.specifications).map(([key, value]) => (
                <div key={key} className="spec-row">
                  <span className="spec-key">{key}:</span>
                  <span className="spec-value">{String(value)}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Product Description & Reviews */}
      <div className="product-details-bottom">
        {/* Description */}
        <div className="product-section full-width">
          <h2>About this item</h2>
          <div className="description-content">
            <p>{product.description}</p>
            <div className="brand-highlight">
              <strong>Top Brand: {product.brand}</strong>
              <p>From the manufacturer: Experience premium quality with {product.brand}'s latest innovation in {product.category}.</p>
            </div>
          </div>
        </div>

        {/* Customer Reviews */}
        <div className="product-section full-width">
          <h2>Customer Reviews</h2>
          <div className="reviews-summary">
            <div className="overall-rating">
              <div className="rating-big">{product.rating}</div>
              <div className="stars-big">{renderStars(product.rating)}</div>
              <div className="review-count">{product.reviewCount} reviews</div>
            </div>
          </div>
          
          <div className="reviews-list">
            {product.reviews.map((review, index: number) => (
              <div key={index} className="review-item">
                <div className="review-header">
                  <span className="reviewer">{review.user}</span>
                  <div className="review-rating">
                    {renderStars(review.rating)}
                    <span className="review-date">{review.date}</span>
                  </div>
                </div>
                <p className="review-comment">{review.comment}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
